        setInputValue(event.target.value);
    };
    function editTask(event) {
        event.preventDefault();
        setEditTask([...task, task.submittedValue]);
    }



    return (
        <div className='task'>
            <h3>{submittedValue}</h3>
            <button onClick={openModal} className='button'>Edit</button>
            {isEditModalOpened && (
                <div className='overlay'>
                    <div className="modal-content">
                        <p>Modal</p>
                        <form onSubmit={editTask} className={`modal ${className}`}>
                            <input type="text" 
                                value={inputValue}
                                onChange={handleInputChange}
                            />
                            <button onClick={closeModal} className='button btnForm'>Change</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Task;
