    function handleInputChange(event) {
        setInputValue(event.target.value);
    }

    const changeTaskHeading = (el) => {
        el.preventDefault();
        const headingValue = el.target.headingname.value;
        changeHeading({ ...headingTask, heading: headingValue })
    }


    console.log(headingTask)
