import React, { useState } from 'react';

import Modal from '../../component/Modal/Modal';
import Button from '../../component/Button/Button';
import './Task.scss'

const Task = ({ submittedValue, inputValue }) => {

    const [isEditModalOpened, setIsEditModalOpened] = useState(false);
    const [edditedSubmit, setEdditedSubmit] = useState([submittedValue]);

    const openModal = () => {
        setIsEditModalOpened(true);
        // console.log{inputValue};
    };

    const closeModal = () => {
        setIsEditModalOpened(false);
    };

    function editSubmit(event) {
        event.preventDefault();
        setEdditedSubmit([{...edditedSubmit, value:inputValue}])
    };

    return (
        <div className='task'>
            <h3>{submittedValue}</h3>
            <button onClick={openModal} className='button'>Edit</button>
            {isEditModalOpened && (
                <Modal 
                    className="anotherModal"
                    closeModal={closeModal}
                    editSubmit={editSubmit}
                />
            )}
        </div>
    );
};

export default Task;
