import { useState } from 'react';

import Input from '../Input/Input';
import Button from '../Button/Button';
import './Modal.scss';

const Modal = ({closeModal}) => {

    const [headingTask, changeHeading] = useState({

    });
    // const [submittedValue, setSubmittedValue] = useState("");

    const changeTaskHeading = (el) => {
        el.preventDefault();
        const heading = el.target.headingName.value;
        changeHeading({ ...headingTask, headingText: heading })
    }

    // function handleSubmit() {
    //     setSubmittedValue(headingText);
    // }


    const onClickHandler = () => {

    }

    return (
        <div className="overlay">
            <div className="modal-content">
                <p>Modal {headingTask.headingText}</p>
                <form onSubmit={changeTaskHeading}>
                    <Input
                        placeholder={'Text here'}
                        name={'headingName'}
                    />
                    <button className='button btnForm' onClick={onClickHandler}>Change</button>
                </form>
                <Button
                    onClick={closeModal}
                >
                    X
                </Button>
            </div>
        </div>
    );
};


export default Modal;
