import React, { useState } from 'react';

import Button from '../../component/Button/Button';
import './Task.scss'

const Task = ({ clicked, submittedValue }) => {

    const [taskTitle, setTaskTitle] = useState("User can edit task");

    const handleEditClick = () => {
      const newTitle = prompt("Enter new task title:");
      setTaskTitle(newTitle || taskTitle);
    };

    return (
        <div className="task">
          <h3>{taskTitle} {submittedValue}</h3>
          <Button clicked={handleEditClick}>Edit</Button>
        </div>
      );

    };


export default Task;
