    function handleSubmit() {
        setSubmittedValue(headingText);
    }


    <Task submittedValue={submittedValue}/>
    return (
        <div className="overlay">
            <div className="modal-content">
                <p>Modal {headingTask.headingText}</p>
                <form onSubmit={changeTaskHeading}>
                    <Input
                        placeholder={'Text here'}
                        name={'headingName'}
                        value={headingText} onChange={handleInputChange}
                    />
                    <button className='button btnForm' onClick={handleSubmit}>Change</button>
                </form>
                <Button
                    onClick={closeModal}
                >
                    X
                </Button>
            </div>
        </div>
    );
};


export default Modal;
