import React from 'react';
import { useState } from 'react';

import Task from '../../Tasks/Task/Task';
import Input from '../Input/Input';
import Button from '../Button/Button';
import './Modal.scss';

const Modal = ({closeModal, changeTaskHeading, headingTask, onClickHandler}) => {
    // console.log(changeTaskHeading);
    console.log(headingTask.headingText)


    return (
        <div className="overlay">
            <div className="modal-content">
                <p>Modal {headingTask.headingText}</p>
                <form onSubmit={changeTaskHeading}>
                    <Input
                        placeholder={'Text here'}
                        name={'headingName'}
                    />
                    <button className='button btnForm' onClick={onClickHandler}>Change</button>
                </form>
                <Button
                    onClick={closeModal}
                >
                    X
                </Button>
            </div>
        </div>
    );
};


export default Modal;
