import React from 'react';
import { useState } from 'react';

import Modal from '../component/Modal/Modal';
import Button from '../component/Button/Button';
import Task from './Task/Task';
import './Tasks.scss'

const Tasks = ({onClick}) => {

    const [isModalOpened, setIsModalOpened] = useState(false);

    const openModal = () => {
        setIsModalOpened(true);
    };

    const closeModal = () => {
        setIsModalOpened(false);
    };

    const [headingTask, changeHeading] = useState({
        headingText: ''
    });

    const [submittedValue, setSubmittedValue] = useState("");

    const changeTaskHeading = (el) => {
        el.preventDefault();
        const heading = el.target.headingName.value;
        changeHeading({ ...headingTask, headingText: heading })
    }



    const onClickHandler = (e) => {
        e.preventDefault();
        setSubmittedValue(headingTask.headingText);
        console.log(headingTask.headingText);

    }

    return (
        <div className='tasks'>
            <Task></Task>
            <Button onClick={openModal}>
                Add Task
            </Button>
            {isModalOpened && <Modal closeModal={closeModal} submittedValue={submittedValue} headingTask={headingTask} changeTaskHeading={changeTaskHeading} />}

        </div>
    );
};


export default Tasks;
