import React from 'react';
import { useState } from 'react';

import Modal from '../component/Modal/Modal';
import Button from '../component/Button/Button';
import Task from './Task/Task';
import './Tasks.scss'

const Tasks = ({onClick}) => {

    const [isModalOpened, setIsModalOpened] = useState(false);

    const openModal = () => {
        setIsModalOpened(true);
    };

    const closeModal = () => {
        setIsModalOpened(false);
    };

    const [inputValue, setInputValue] = useState("");
    const [submittedValue, setSubmittedValue] = useState("");

    function handleInputChange(event) {
        setInputValue(event.target.value);
    }

    function handleSubmit(event) {
        event.preventDefault();
        setSubmittedValue(inputValue);
    }

    const [isTaskVisible, setTaskVisible] = useState(false);

    function handleButtonClick() {
      setTaskVisible(true);
    }

    function handleTaskSubmit(value) {
      setSubmittedValue(value);
      setTaskVisible(false);
    }

    return (
        <div className='tasks'>
            {isTaskVisible ? (
                <Task submittedValue={submittedValue} onSubmit={handleTaskSubmit} />
            ) : (
                <button onClick={handleButtonClick}>Show Task</button>
            )}
            <Task submittedValue={submittedValue}></Task>
            <Button onClick={openModal}>
                Add Task
            </Button>
            {isModalOpened && <Modal closeModal={closeModal} handleSubmit={handleSubmit} handleInputChange={handleInputChange} />}

        </div>
    );
};


export default Tasks;
