import { useState } from 'react';

import Input from '../Input/Input';
import Button from '../Button/Button';
import './Modal.scss';

// const [onChange, changed] = useState();
const Modal = ({closeModal}) => {

    const [valueInput, changeValue] = useState({
        value: ''
    });

    const changeTaskHeading = (el) => {
        el.preventDefault();
        const taskHeading = el.target.taskHeading.value;
        changeValue({ ...valueInput, value: taskHeading })
    }

    const onClickHandler = () => {

    }

    return (
        <div className="overlay">
            <div className="modal-content">
                <p>{valueInput.value}</p>
                <p>Modal</p>
                <form onSubmit={changeTaskHeading}>
                    <Input
                        placeholder={'Text here'}
                    >

                    </Input>
                    <button onClick={onClickHandler}>Change</button>
                </form>
                <Button
                    onClick={closeModal}
                >
                    X
                </Button>
            </div>
        </div>
    );
};


export default Modal;
