import React, { useState } from 'react';

import Modal from '../../component/Modal/Modal';
import Button from '../../component/Button/Button';
import './Task.scss'

const Task = ({ clicked, submittedValue, openModal }) => {

    const [editedValue, setEditedValue] = useState(submittedValue);

    const handleModalSubmit = (event, value) => {
        event.preventDefault();
        setEditedValue(submittedValue || value);
    };

    // const handleEditClick = () => {
    //     const newTitle = prompt("Enter new task title:");
    //     setTaskTitle(newTitle || taskTitle);
    //   };

    <Modal
        closeModal={handleModalClose}
        handleSubmit={handleModalSubmit}
        initialValue={editedValue}
    />
    return (
        <div className='task'>
            <h3>{submittedValue}</h3>
            <button onClick={openModal} className='button'>Edit</button>
            {/* {isModalOpened && (
            )} */}
        </div>
    );
};

export default Task;
