import React from 'react';
import { useState } from 'react';

import Task from '../../Tasks/Task/Task';
import Input from '../Input/Input';
import Button from '../Button/Button';
import './Modal.scss';

const Modal = ({closeModal}) => {

    const [headingTask, changeHeading] = useState("");

    const [submittedValue, setSubmittedValue] = useState("");

    const changeTaskHeading = (el) => {
        el.preventDefault();
        const heading = el.target.headingName.value;
        changeHeading({ ...headingTask, headingText: heading })
        // setInputValue(el.target.headingName.value);
    }



    const onClickHandler = () => {
        setSubmittedValue(headingTask);
        console.log(headingTask.headingText);

    }

    const onChangeInput = () => {
        console.log("Worked!")
    }

    <Task submittedValue={submittedValue}/>
    return (
        <div className="overlay">
            <div className="modal-content">
                <p>Modal {headingTask.headingText}</p>
                <input type="text" />
                <form onSubmit={changeTaskHeading}>
                    <Input
                        placeholder={'Text here'}
                        name={'headingName'}
                        onChange={onChangeInput}
                    />
                    <button className='button btnForm' onClick={onClickHandler}>Change</button>
                </form>
                <Button
                    onClick={closeModal}
                >
                    X
                </Button>
            </div>
        </div>
    );
};


export default Modal;
